sys_variable_value.value:
(function execute(inputs, outputs) {
 if (parseInt(inputs.first_glacier) <= 0 || parseInt(inputs.first_glacier) > 132890) {
    outputs.first_glacier_to_get = "1" 
 } else {
    outputs.first_glacier_to_get = inputs.first_glacier;
 }

  if (parseInt(inputs.glaciers_per_page) <= 0 || parseInt(inputs.glaciers_per_page) > 10) {
      outputs.glaciers_per_page = "1";
      }else {
       outputs.glaciers_per_page = inputs.glaciers_per_page;    
  }

  if (parseInt(inputs.pages_to_get) <= 0){
    outputs.number_of_pages = "1";
  }else {
    outputs.number_of_pages = inputs.pages_to_get;
  }


})(inputs, outputs);

